import React, { Component } from 'react';

/**
 * NOTE seems @types/react is not handling defaultProps for
 * class components properly, so we take following approach.
 */
interface OwnProps {
    defaultValue?: string;
    id: string;
    inline?: boolean;
    inputRef?: (...args: any[]) => void;
    name?: string;
    onInput?: (...args: any[]) => void;
    type?: string;
    /**
     * Can be passed down from `Field` component.
     */
    errorMessages?: any;
}
const defaultProps = {
    defaultValue: '',
    inline: false,
    inputRef: () => {},
    onInput: () => {},
    type: 'text',
};
// Refine e.g. `inline?: boolean` to `inline: boolean`.
type Props = OwnProps & typeof defaultProps;

interface State {
    hasValue: boolean;
}

class Input extends Component<Props, State> {
    // eslint-disable-next-line react/sort-comp
    static defaultProps = defaultProps;

    state = {
        hasValue: !!this.props.defaultValue,
    };

    /** Initialised in `handleInputRef`. */
    private input!: HTMLInputElement;

    handleClear = () => {
        this.input.value = '';
        const event = new Event('input', { bubbles: true });
        this.input.dispatchEvent(event);
        this.input.focus();
    };

    handleInput = (event: any) => {
        this.setState({
            hasValue: !!this.input.value,
        });
        const { onInput } = this.props;
        onInput(event);
    };

    handleInputRef = (element: HTMLInputElement) => {
        this.input = element;
        const { inputRef } = this.props;
        inputRef(element);
    };

    render() {
        const {
            id,
            inline,
            name = id,
            type,
            inputRef: _,
            errorMessages: __,
            ...props
        } = this.props;

        const { hasValue } = this.state;

        return (
            <>
                <input
                    {...props}
                    type="input"
                    id={id}
                    name={name}
                    onChange={this.handleInput}
                    ref={this.handleInputRef}
                />
                {hasValue && (
                    <button
                        aria-label="Clear input"
                        type="button"
                        className="input-clear"
                        onClick={this.handleClear}
                    >x</button>
                )}
            </>
        );
    }
}

export default Input;
