const initialState = {
	isLightboxOpen: false,
	slideNumber: 0
};

const reducer = (state = initialState, action: any) => {
	switch (action.type) {
	case 'OPEN_LIGHTBOX':
		return {
			...state,
			...{
				isLightboxOpen: true,
				slideNumber: action.slideNumber
			},
		};
	case 'CLOSE_LIGHTBOX':
		return {
			...state,
			...{
				isLightboxOpen: false,
			},
		};
	default:
		return state;
	}
};

export default reducer;
