import React from 'react';
import { useDispatch } from 'react-redux';

import {actions} from '../redux/actions';

interface Props {
    value?: string;
}

const SearchButton: React.FC<Props> = (props) => {
    const sendEvent = (value: string | undefined, dis: (no: any) => void) => {
        dis(44);

        fetch(
            `https://eutils.ncbi.nlm.nih.gov/entrez/eutils/esearch.fcgi?db=pubmed&retmax=20&retmode=json&term=${value}`
        )
            .then((response) => response.json())
            .then((data) => console.log(data));
    };

    const { value } = props;
    const dispatch = useDispatch();
    const dis = dispatch(actions.openLightboxAction);
    return (
        <button className="input-search" onClick={() => sendEvent(value, dis)}>
            Search now
        </button>
    );
};

export default SearchButton;
