import { UPDATE_QUERY_RESULT, UPDATE_DATA } from './actions';

export default (state = {}, action) => {
    switch (action.type) {
        case UPDATE_QUERY_RESULT:
            return {
                ...state,
                count: action.count
            };
        case UPDATE_DATA:
            return {
                ...state,
                searchResult: action.results
            };
        default:
            return state;
    }
};
