import React from 'react';
import { useDispatch } from 'react-redux';

import {actions} from '../redux/actions';

interface Props {
    value?: string;
}

const SearchButton: React.FC<Props> = (props) => {
    const sendEvent = (value: string | undefined) => {
        fetch(
            `https://eutils.ncbi.nlm.nih.gov/entrez/eutils/esearch.fcgi?db=pubmed&retmax=20&retmode=json&term=${value}`
        )
            .then((response) => response.json())
            .then((data) => console.log(data));
    };

    const { value } = props;
    const dispatch = useDispatch();
    dispatch(actions.openLightboxAction)
    return (
        <button className="input-search" onClick={() => sendEvent(value)}>
            Search now
        </button>
    );
};

export default SearchButton;
