import React from 'react';
import { useDispatch } from 'react-redux';
import { sendEvent } from '../api';

import getUrlParameter from '../util/get-param';

interface Props {
    value?: string;
}

const SearchButton: React.FC<Props> = (props) => {
    const { value } = props;
    const dispatch = useDispatch();
    const windo = getUrlParameter(window.location.search, 'title');

    console.log(windo);

    return (
        <button
            className="input-search"
            onClick={() => {
                sendEvent(value, dispatch);
                window.history.pushState(null, 'title', `?title=${value}`);
            }}>
            Search now
        </button>
    );
};

SearchButton.defaultProps = {
    value: '',
}

export default SearchButton;
