import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getCount, getIds } from '../redux/actions';

// interface Props {
//     value?: string;
// }

const SearchButton = (props) => {
    const {
        data: { ids },
    } = useSelector((state) => state);

    const listJournalsEvent = async () => {
        const idJoin = ids.join(',');
        // fetch(
        //     `https://eutils.ncbi.nlm.nih.gov/entrez/eutils/esummary.fcgi?db=pubmed&retmode=json&id=${idJoin}`
        // )
        //     .then((response) => response.json())
        //     .then((data) => {
        //         console.log(data);
        //     });
    };

    const sendEvent = async (value, dispatch) => {
        const response = await fetch(`https://eutils.ncbi.nlm.nih.gov/entrez/eutils/esearch.fcgi?db=pubmed&retmax=20&retmode=json&term=${value}`);
        const json = await response.json();
        dispatch(getCount(json));
        dispatch(getIds(json));
        listJournalsEvent();
    };

    const { value } = props;
    const dispatch = useDispatch();

    return (
        <button
            className="input-search"
            onClick={() => {
                sendEvent(value, dispatch);
            }}>
            Search now
        </button>
    );
};

export default SearchButton;
